package com.london.doctour.presentation.ui.fragments.mainimport androidx.core.content.ContextCompatimport androidx.navigation.NavControllerimport androidx.navigation.ui.NavigationUI.setupWithNavControllerimport by.kirich1409.viewbindingdelegate.viewBindingimport com.london.doctour.base.BaseFlowFragmentimport com.london.doctour.Rimport com.london.doctour.databinding.FragmentMainFlowBindingclass MainFlowFragment :    BaseFlowFragment(R.layout.fragment_home, R.id.nav_host_fragment_main) {    private val binding by viewBinding(FragmentMainFlowBinding::bind)    override fun setupNavigation(navController: NavController) {        constructBottomNavigation(navController)    }    private fun constructBottomNavigation(navController: NavController) {        setupWithNavController(binding.bottomNavigation, navController)        binding.bottomNavigation.itemActiveIndicatorColor =            ContextCompat.getColorStateList(requireContext(), R.color.home_fragment_color)        binding.bottomNavigation.setOnItemSelectedListener { item ->            when (item.itemId) {                R.id.homeFragment -> {                    navController.navigate(R.id.homeFragment)                    true                }                R.id.searchFragment -> {                    navController.navigate(R.id.searchFragment)                    true                }                R.id.fragment_profile -> {                    navController.navigate(R.id.fragment_profile)                    true                }                else -> false            }        }    }}